let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));

openAddDeviceDialog();

function openAddDeviceDialog() {
    console.log("HTMl template: ", htmlTemplate);
    customDialog.customDialog(htmlTemplate, AddDeviceDialogController).subscribe();
}

function AddDeviceDialogController(instance) {
    
    let vm = instance;
    // vm.config = "AAAA";
        vm.productionLines = [
        {
            "key": "description",
            "name": "Mô tả nhóm sản phẩm",
            "type": "string"
        }
    ];
    vm.productionLineForm = vm.fb.group({
      name: ['', [vm.validators.required]],
      label: ['', vm.validators.required],
      attributes: vm.fb.group({
      })
    });
    items = vm.productionLineForm.get('attributes');
    vm.productionLines.forEach(c => {
        items.addControl(c.key, vm.fb.control('', vm.validators.required));
    });
    vm.productionLineForm.setControl('attributes', items);
    
    vm.cancel = function() {
        vm.dialogRef.close(null);
    };
    
    vm.save = function() {
        vm.productionLineForm.markAsPristine();
        let asset = {
            name: vm.productionLineForm.get('name').value,
            type: 'groupProducts',
            label: vm.productionLineForm.get('label').value
        };
        assetService.saveAsset(asset).subscribe(
            function (asset) {
                saveAttributes(asset.id).subscribe(
                    function () {
                        widgetContext.updateAliases();
                        vm.dialogRef.close(null);
                    }
                );
            }
        );
    };
    
    function saveAttributes(entityId) {
        let attributes = vm.productionLineForm.get('attributes').value;
        let attributesArray = [];
        for (let key in attributes) {
            attributesArray.push({key: key, value: attributes[key]});
        }
        if (attributesArray.length > 0) {
            return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }
}

