function openDeleteAssetDialog() {
    let title = "Bạn có muốn xóa lỗi " + entityName +  "?";
    let content = "Hãy cẩn thận, mọi thông tin về lỗi  sẽ bị xóa";
    dialogs.confirm(title, content, 'Cancel', 'Delete').subscribe(
        function (result) {
            if (result) {
                deleteAsset();
            }
        }
    );
}

function deleteAsset() {
    assetService.deleteAsset(entityId.id).subscribe(
        function () {
            widgetContext.updateAliases();
        }
    );
}
