            // speed: [{value: '', disabled: true}],
            // reliability: [{value: '', disabled: true}],
            // totalError: [{value: '', disabled: true}]
  });
  self.updateForm();


}

self.updateForm = function() {
    console.log("self.ctx.data: ", self.ctx.data);
    if(self.ctx.data[0].data[0]) {
        $scope.oee = self.ctx.data[0].data[0][1];
        var timeStart = self.secondsToHms(self.ctx.data[1].data[0][1]);
        var timeRunning = self.secondsToHms(self.ctx.data[2].data[0][1]);
        var stopTime = self.secondsToHms(self.ctx.data[3].data[0][1]);
        var numberOfInput = self.ctx.data[4].data[0][1];
        var averageProductivity = (numberOfInput*3600/self.ctx.data[2].data[0][1]).toFixed(2);
        $scope.dashboardForm.setValue({
            "timeStart": timeStart,
            "timeRunning": timeRunning,
            "timeStop": stopTime,
            "averageProductivity": averageProductivity
        })
  }
}

self.secondsToHms = function(d) {
    if(d==0) return "00:00:00"
    d = Number(d);
    var h = Math.floor(d / 3600);
    var m = Math.floor(d % 3600 / 60);
    var s = Math.floor(d % 3600 % 60);
    //var hDisplay = h > 0 ? h + (h == 1 ? " hour, " : " hours, ") : "";
    //var mDisplay = m > 0 ? m + (m == 1 ? " minute, " : " minutes, ") : "";
    //var sDisplay = s > 0 ? s + (s == 1 ? " second" : " seconds") : "";
    var hDisplay = h > 0 ? h + (h == 1 ? ":" : ":") : "";
    var mDisplay = m > 0 ? m + (m == 1 ? ":" : ":") : "";
    var sDisplay = s > 0 ? s + (s == 1 ? "" : "") : "";
        return hDisplay + mDisplay + sDisplay; 
}

