{
    "schema": {
        "type": "object",
        "title": "EntitiesTableSettings",
        "properties": {
            "widgetTitle": {
                "title": "Widget title",
                "type": "string",
                "default": ""
            },
            "showLabel":{
                "title":"Show label",
                "type":"boolean",
                "default":true
            },
