self.onInit = function() {

    var pieData = {
        labels: [],
        datasets: []
    };

    var dataset = {
        data: [],
        backgroundColor: [],
        borderColor: [],
        borderWidth: [],
        hoverBackgroundColor: []
    }

    pieData.datasets.push(dataset);

    for (var i=0; i < self.ctx.data.length; i++) {
        var dataKey = self.ctx.data[i].dataKey;
        var units = dataKey.units && dataKey.units.length ? dataKey.units : self.ctx.units;
        units = units ? (' (' + units + ')') : '';
        pieData.labels.push(dataKey.label + units);
        dataset.data.push(0);
        var hoverBackgroundColor = tinycolor(dataKey.color).lighten(15);
        var borderColor = tinycolor(dataKey.color).darken();
        dataset.backgroundColor.push(dataKey.color);
        dataset.borderColor.push('#fff');
        dataset.borderWidth.push(5);
        dataset.hoverBackgroundColor.push(hoverBackgroundColor.toRgbString());
    }

    var ctx = $('#pieChart', self.ctx.$container);
    self.ctx.chart = new Chart(ctx, {
        type: 'pie',
        data: pieData,
        options: {
            responsive: false,
            maintainAspectRatio: false
        }
    });    

    self.onResize();
}

