descriptor.controllerScript:
self.onInit = function() {
    var pieData = {
        labels: [],
        datasets: []
    };

    var dataset = {
        data: [],
        backgroundColor: [],
        borderColor: [],
        borderWidth: [],
        hoverBackgroundColor: []
    }

    var borderColor = self.ctx.settings.borderColor || '#fff';
    var borderWidth = typeof self.ctx.settings.borderWidth !== 'undefined' ? self.ctx.settings.borderWidth : 5;

    pieData.datasets.push(dataset);

    for (var i=0; i < self.ctx.data.length; i++) {
        var dataKey = self.ctx.data[i].dataKey;
        var units = dataKey.units && dataKey.units.length ? dataKey.units : self.ctx.units;
        units = units ? (' (' + units + ')') : '';
        pieData.labels.push(dataKey.label + units);
        dataset.data.push(0);
        var hoverBackgroundColor = tinycolor(dataKey.color).lighten(15);
        dataset.backgroundColor.push(dataKey.color);
        dataset.borderColor.push(borderColor);
        dataset.borderWidth.push(borderWidth);
        dataset.hoverBackgroundColor.push(hoverBackgroundColor.toRgbString());
    }

    var options = {
        responsive: false,
        maintainAspectRatio: false,
        legend: {
            display: true,
            labels: {
                fontColor: '#666'
            }
        },
        tooltips: {
            callbacks: {
                label: function(tooltipItem, data) {
                    var label = data.labels[tooltipItem.index];
                    var value = data.datasets[tooltipItem.datasetIndex].data[tooltipItem.index];
                    var content = label + ': ' + value;
                    var units = self.ctx.settings.units ? self.ctx.settings.units : self.ctx.units;
                    if (units) {
                        content += ' ' + units;
                    } 
                    return content;
                }
            }
        }
    };

    if (self.ctx.settings.legend) {
        options.legend.display = self.ctx.settings.legend.display !== false;
        options.legend.labels.fontColor = self.ctx.settings.legend.labelsFontColor || '#666';
    }

    var ctx = $('#pieChart', self.ctx.$container);
    self.ctx.chart = new Chart(ctx, {
        type: 'doughnut',
        data: pieData,
        options: options
    });

    self.onResize();
}

self.onDataUpdated = function() {
    for (var i = 0; i < self.ctx.data.length; i++) {
        var cellData = self.ctx.data[i];
        if (cellData.data.length > 0) {
            var decimals;
            if (typeof cellData.dataKey.decimals !== 'undefined' 
                && cellData.dataKey.decimals !== null ) {
                decimals = cellData.dataKey.decimals; 
            } else {
                decimals = self.ctx.decimals;
            }
            var tvPair = cellData.data[cellData.data.length - 1];
            var value = self.ctx.utils.formatValue(tvPair[1], decimals);
            self.ctx.chart.data.datasets[0].data[i] = parseFloat(value);
        }
    }
    self.ctx.chart.update();
}

self.onResize = function() {
    self.ctx.chart.resize();
}

