descriptor.controllerScript:
let data;
let ChartDataLabels;
self.onInit = function() {
    data = {
        labels: ["Sản lượng kế  hoạch",
            "Sản lượng hoàn thành",
            "Thời gian chạy",
            "Thời gian kế hoạch"
        ],
        datasets: [{
                backgroundColor: ['#38227d',
                    'hsl(180, 100%, 35%)'
                ],
                data: []
            },
            {
                backgroundColor: ['#f44336',
                    '#4caf50'
                ],
                data: []
            }

        ]
    };

    console.log("Data multiSeriesChart: ", self.ctx
        .data);
    if (self.ctx.data[0].data.length > 0) {
        console.log("AAAAAAAAAAA");
        var numberOfOutput = parseFloat(self.ctx.data[0]
            .data[0][1]) ? parseFloat(self.ctx.data[
                0]
            .data[0][1]) : 0;
        var numberOfPlanning = parseFloat(self.ctx.data[
                1]
            .data[0][1]) ? parseFloat(self.ctx.data[
                1]
            .data[0][1]) : 0;
        var runningTime = parseFloat(self.ctx.data[2]
            .data[0][1]) ? parseFloat(self.ctx.data[
                2]
            .data[0][1]) : 0;
        var planningTime = parseFloat(self.ctx.data[3]
            .data[0][1]) ? parseFloat(self.ctx.data[
                3]
            .data[0][1]) : 0;
        var totalNumber = (numberOfPlanning +
            numberOfOutput) != 0 ? (
            numberOfPlanning +
            numberOfOutput) : 1;
        var totalTime = (runningTime +
            planningTime) != 0 ? (runningTime +
            planningTime) : 1;
        data.datasets[0].data.push(numberOfOutput /
            totalNumber * 100);
        data.datasets[0].data.push(numberOfPlanning /
            totalNumber * 100);
        data.datasets[1].data.push(runningTime /
            totalTime * 100);
        data.datasets[1].data.push(planningTime /
            totalTime * 100);

    }
    console.log(data);

    const config = {
        type: 'doughnut',
        data: data,
        plugins: [ChartDataLabels ? ChartDataLabels: ''],
        options: {
            responsive: false,
            maintainAspectRatio: false,
            plugins: {
                datalabels: {
                    display: true,
                    formatter: (value) => {
                        return Math.round(
                                value * 100) /
                            100 + '%';
                    },
                    color: "#fff"
                },

                legend: {
                    labels: {
                        display: true,
                        generateLabels: function(
                            chart) {
                            // Get the default label list
                            const original =
                                Chart.overrides
                                .pie.plugins
                                .legend.labels
                                .generateLabels;
                            const
                                labelsOriginal =
                                original.call(
                                    this, chart
                                );

                            // Build an array of colors used in the datasets of the chart
                            let datasetColors =
                                chart.data
                                .datasets.map(
                                    function(
                                        e) {
                                        return e
                                            .backgroundColor;
                                    });
                            datasetColors =
                                datasetColors
                                .flat();

                            // Modify the color and hide state of each label
                            labelsOriginal
                                .forEach(
                                    label => {
                                        // There are twice as many labels as there are datasets. This converts the label index into the corresponding dataset index
                                        label
                                            .datasetIndex =
                                            (label
                                                .index -
                                                label
                                                .index %
                                                2
                                            ) /
                                            2;

                                        // The hidden state must match the dataset's hidden state
                                        label
                                            .hidden = !
                                            chart
                                            .isDatasetVisible(
                                                label
                                                .datasetIndex
                                            );

                                        // Change the color to match the dataset
                                        label
                                            .fillStyle =
                                            datasetColors[
                                                label
                                                .index
                                            ];
                                    });

                            const chartTitle =
                                labelsOriginal
                                .filter(
                                    function(
                                        value,
                                        index,
                                        arr) {
                                        if (index ==
                                            0) {
                                            value
                                                .text =
                                                "Sản lượng";
                                            return value;
                                        }
                                        if (index ==
                                            2) {
                                            value
                                                .text =
                                                "Thời gian";
                                            return value;
                                        }
                                    })
                            console.log(
                                labelsOriginal
                            )
                            return chartTitle;
                        }
                    },
                    onClick: function(mouseEvent,
                        legendItem, legend) {
                        // toggle the visibility of the dataset from what it currently is
                        legend.chart
                            .getDatasetMeta(
                                legendItem
                                .datasetIndex
                            ).hidden = legend
                            .chart
                            .isDatasetVisible(
                                legendItem
                                .datasetIndex);
                        legend.chart.update();
                    }
                },
                tooltip: {
                    callbacks: {
                        label: function(context) {
                            const labelIndex = (
                                    context
                                    .datasetIndex *
                                    2) + context
                                .dataIndex;
                            return context.chart
                                .data.labels[
                                    labelIndex
                                ] + ': ' +
                                context
                                .formattedValue;
                        }
                    }
                }
            }
        }
    };
    var ctx = $('#multiSeriesChart', self.ctx
        .$container);
    self.ctx.chart = new Chart(ctx, config);
    self.onResize();
}

self.onDataUpdated = function() {
    var data1 = [];
    var data2 = [];
    console.log("Data multiSeriesChart: ", self.ctx
        .data);
    if (self.ctx.data[0].data.length > 0) {
        console.log("AAAAAAAAAAA");
        var numberOfOutput = parseFloat(self.ctx.data[0]
            .data[0][1]) ? parseFloat(self.ctx.data[
                0]
            .data[0][1]) : 0;
        var numberOfPlanning = parseFloat(self.ctx.data[
                1]
            .data[0][1]) ? parseFloat(self.ctx.data[
                1]
            .data[0][1]) : 0;
        var runningTime = parseFloat(self.ctx.data[2]
            .data[0][1]) ? parseFloat(self.ctx.data[
                2]
            .data[0][1]) : 0;
        var planningTime = parseFloat(self.ctx.data[3]
            .data[0][1]) ? parseFloat(self.ctx.data[
                3]
            .data[0][1]) : 0;
        var totalNumber = (numberOfPlanning +
            numberOfOutput) != 0 ? (
            numberOfPlanning +
            numberOfOutput) : 1;
        var totalTime = (runningTime +
            planningTime) != 0 ? (runningTime +
            planningTime) : 1;
        const data1 = [numberOfOutput / totalNumber *
            100,
            numberOfPlanning / totalNumber * 100
        ];
        const data2 = [runningTime /
            totalTime * 100,
            planningTime /
            totalTime * 100
        ];
        data.datasets[0].data = data1;
        data.datasets[1].data = data2;

    }
    // console.log("Self data ctx: ", self.ctx.data);
    // for (var i = 0; i < self.ctx.data.length; i++) {
    //     var cellData = self.ctx.data[i];
    //     if (cellData.data.length > 0) {
    //         var decimals;
    //         if (typeof cellData.dataKey.decimals !== 'undefined' 
    //             && cellData.dataKey.decimals !== null ) {
    //             decimals = cellData.dataKey.decimals; 
    //         } else {
    //             decimals = self.ctx.decimals;
    //         }
    //         var tvPair = cellData.data[cellData.data.length - 1];
    //         var value = self.ctx.utils.formatValue(tvPair[1], decimals);
    //         self.ctx.chart.data.datasets[0].data[i] = parseFloat(value);
    //     }
    // }
    self.ctx.chart.update();

}

self.onResize = function() {
    self.ctx.chart.resize();
}

