let data;
self.onInit = function() {
    // var demo = $('#demo', self.ctx.$container);
    console.log("Self.ctx: ", self.ctx);
    // self.ctx.demo = self.ctx;
    // // self.ctx.demo.$containerParent[0] = demo;
    // console.log("Self.ctx.demo: ", demo);
    // self.ctx.flot = new TemPeratureFlot(self.ctx.demo);
    const labels = [
        'Vùng 1',
        'Vùng 2',
        'Vùng 3'
    ];
    data = {
        labels: labels,
        datasets: [{
                label: 'Nhiệt độ đo',
                backgroundColor: 'rgb(24, 176, 35)',
                borderColor: 'rgb(24, 176, 35)',
                data: [],
            },
            {
                label: 'Tiêu chuẩn',
                backgroundColor: 'rgb(255, 0, 0)',
                borderColor: 'rgb(255, 0, 0)',
                data: []
            }
        ]
    };
    for (var i = 0; i < self.ctx.data.length; i++) {
        console.log("self data ctx", self.ctx.data[i]
            .data);
        if (self.ctx.data[i].data.length > 0) {
            if (i < 3) {
                data.datasets[0].data.push(self.ctx
                    .data[i].data[0][1]);
            } else
                data.datasets[1].data.push(self.ctx
                    .data[i].data[0][1]);

        }
    }
    console.log("DAtaset: ", data.datasets[0].data);

    const config = {
        type: 'line',
        data: data,
        options: {
            maintainAspectRatio: false,
            responsive: false
        }
    };
    var ctx = $('#lineArea', self.ctx.$container);
    // console.log("Config Line chart: ", ctx);
    // var ctx = $('#demo', self.ctx.$container);
    // console.log("Config Line chart: ", ctx);
    self.ctx.chart = new Chart(ctx, config);
    self.onResize();
    // // console.log("Self data ctx: ", self.ctx.data);
    // self.ctx..demo.flot = new TbFlot(self.ctx);
    // var demo = $('#demo', self.ctx.$container);
    // console.log("Demo id: ", demo);
    // self.ctx.demo = self.ctx;
    // self.ctx.demo.flot = new TbFlot(self.ctx.demo);
    // self.ctx.demo.$container = ctx;
    // self.ctx.demo.flot.$element = ctx;
    // console.log("self.ctx.demo:", self.ctx.demo);
    // console.log("self.ctx.demo.flot: ", self.ctx.demo
    //     .flot);
    // self.ctx.$container[0].innerHTML = ""
}

self.onDataUpdated = function() {
    var data1 = [];
    var data2 = [];
    for (var i = 0; i < self.ctx.data.length; i++) {
        // console.log("self data ctx", self.ctx.data[i]
        // .data);
        if (self.ctx.data[i].data.length > 0) {
            if (i < 3) {
                data1.push(self.ctx
                    .data[i].data[0][1]);
            } else
                data2.push(self.ctx
                    .data[i].data[0][1]);

        }
    }
    data.datasets[0].data = data1;
    data.datasets[1].data = data2;
    console.log("Self data ctx: ", self.ctx.data);
    for (var i = 0; i < self.ctx.data.length; i++) {
        var cellData = self.ctx.data[i];
        if (cellData.data.length > 0) {
            var decimals;
            if (typeof cellData.dataKey.decimals !== 'undefined' 
                && cellData.dataKey.decimals !== null ) {
                decimals = cellData.dataKey.decimals; 
            } else {
                decimals = self.ctx.decimals;
            }
            var tvPair = cellData.data[cellData.data.length - 1];
            var value = self.ctx.utils.formatValue(tvPair[1], decimals);
            self.ctx.chart.data.datasets[0].data[i] = parseFloat(value);
        }
    }
    self.ctx.chart.update();
}
// self.onDataUpdated = function() {
//     self.ctx.flot.update();
// }

self.onResize = function() {
    // self.ctx.flot.resize();
    self.ctx.chart.resize();
}

// self.onEditModeChanged = function() {
//     self.ctx.flot.checkMouseEvents();
// }

