//     return TbFlot.settingsSchema('graph');
// }

// self.getDataKeySettingsSchema = function() {
//     return TbFlot.datakeySettingsSchema(true, 'graph');
// }

self.onDestroy = function() {
    // self.ctx.flot.destroy();
    self.ctx.chart.destroy();
}

