descriptor.controllerScript:
let $scope;
let settings;
let attributeService;
let utils;
let translate;
let $container;
let dialog;

self.onInit = function() {
    self.ctx.ngZone.run(function() {
        init();
        self.ctx.detectChanges(true);
    });
};


function init() {

    $scope = self.ctx.$scope;
    $container = self.ctx.$container;
    attributeService = $scope.$injector.get(self.ctx
        .servicesMap.get('attributeService'));
    entityRelationService = $scope.$injector.get(self.ctx
        .servicesMap.get('entityRelationService'));
    assetService = $scope.$injector.get(self.ctx
        .servicesMap.get('assetService'));
    utils = $scope.$injector.get(self.ctx.servicesMap.get(
        'utils'));
    translate = $scope.$injector.get(self.ctx.servicesMap
        .get('translate'));
    customDialog = $scope.$injector.get(self.ctx.servicesMap
        .get('customDialog'));
    $scope.toastTargetId = 'input-widget' + utils.guid();
    settings = utils.deepClone(self.ctx.settings) || {};
    settings.showLabel = utils.defaultValue(settings
        .showLabel, true);
    settings.showResultMessage = utils.defaultValue(settings
        .showResultMessage, true);
    $scope.settings = settings;
    $scope.isValidParameter = true;
    $scope.dataKeyDetected = false;

    $scope.requiredErrorMessage = utils.customTranslation(
        settings.requiredErrorMessage, settings
        .requiredErrorMessage) || translate.instant(
        'widgets.input-widgets.entity-attribute-required'
    );
    $scope.labelValue = utils.customTranslation(settings
            .labelValue, settings.labelValue) || translate
        .instant('widgets.input-widgets.value');

    $scope.attributeUpdateFormGroup = $scope.fb.group({
        currentValue: [undefined, [$scope.validators
            .required,
            $scope.validators.min(settings
                .minValue),
            $scope.validators.max(settings
                .maxValue)
        ]],
        name: [self.ctx.data[0].data[0]],
        personMobile: ['']
    });


    if (self.ctx.datasources && self.ctx.datasources
        .length) {
        var datasource = self.ctx.datasources[0];
        if (datasource.type === 'entity') {
            if (datasource.entityType && datasource
                .entityId) {
                $scope.entityName = datasource.entityName;
                if (settings.widgetTitle && settings
                    .widgetTitle.length) {
                    $scope.titleTemplate = utils
                        .customTranslation(settings
                            .widgetTitle, settings
                            .widgetTitle);
                } else {
                    $scope.titleTemplate = self.ctx
                        .widgetConfig.title;
                }

                $scope.entityDetected = true;
            }
        }
        if (datasource.dataKeys.length) {
            if (datasource.dataKeys[0].type !==
                "attribute") {
                $scope.isValidParameter = false;
            } else {
                $scope.currentKey = datasource.dataKeys[0]
                    .name;
                $scope.dataKeyType = datasource.dataKeys[0]
                    .type;
                $scope.dataKeyDetected = true;
            }
        }
    }

    self.ctx.widgetTitle = utils.createLabelFromDatasource(
        self.ctx.datasources[0], $scope.titleTemplate);
    console.log(utils.createLabelFromDatasource(self.ctx
        .datasources[0], $scope.titleTemplate));
    console.log("datasources: ", self.ctx.data[1]);
    $scope.data = self.ctx.data;
    if ($scope.data[1]) {
        console.log(self.ctx.data[3]);
        $scope.personMobile = $scope.data[1].data[0][1];
        $scope.name = $scope.data[0].data[0][1];
        $scope.staffNumber = $scope.data[2].data[0][1];
    }
    console.log("currentKey: ", datasource);
    // $scope.attributeUpdateFormGroup.get('name').patchValue(self.ctx.data[0].data[0]);
    // if(datasource.dataKeys.length);
    $scope.relationData = [
        // device: []
    ];
    //entityRelationService.findInfoByFrom(datasource.entity.id)
	var querry = {
  "filters": [
    {
      "relationType": "Contains",
      "entityTypes": [
        "ASSET"
      ]
    }
  ],
  "parameters": {
    "entityId": {
      "entityType": "ASSET",
      "id": "d6f311a0-5cb6-11ec-83a4-875df4459841"
    },
    "rootId": "d6f311a0-5cb6-11ec-83a4-875df4459841",
    "rootType": "ASSET",
    "direction": "FROM",
    "relationTypeGroup": "COMMON",
    "maxLevel": 2,
    "fetchLastLevelOnly": true
  }
}
//querry.parameters.entityId.id = datasource.entity.id;
//querry.parameters.rootId = datasource.entity.id;
	entityRelationService.findInfoByQuery(querry)
        .subscribe(data => {
            $scope.relationData = data;
            data.forEach((relation, index) => {
                entityRelationService
                    .findInfoByFrom(relation.to)
                    .subscribe(device => {
                        if (device.length >
                            0)
                            $scope
                            .relationData[
                                index]
                            .device =
                            device;
                    })
            })
            console.log("relationData: ", $scope
                .relationData);
        });


    // $scope.updateAttribute = function(entity, index) {
    //     $scope.isFocused = false;
    //     var state = {
    //         "Cmd": index,
    //         "Alert": 0,
    //     };
    //     console.log($scope.data);
    //     $scope.data.forEach(d => console.log(
    //         "attribute state: ", d));
    //     if ($scope.entityDetected) {
    //         var datasource = self.ctx.datasources[0];

    //         attributeService.saveEntityAttributes(
    //             entity,
    //             'SERVER_SCOPE',
    //             [{
    //                 key: $scope.currentKey,
    //                 value: index
    //             }]
    //         ).subscribe(
    //             function success() {
    //                 console.log("Update Success");
    //                 $scope.originalValue = $scope
    //                     .attributeUpdateFormGroup
    //                     .get('currentValue').value;
    //                 if (settings
    //                     .showResultMessage) {
    //                     $scope.showSuccessToast(
    //                         translate.instant(
    //                             'widgets.input-widgets.update-successful'
    //                         ), 1000,
    //                         'bottom', 'left',
    //                         $scope.toastTargetId
    //                     );
    //                 }
    //             },
    //             function fail() {
    //                 if (settings
    //                     .showResultMessage) {
    //                     $scope.showErrorToast(
    //                         translate.instant(
    //                             'widgets.input-widgets.update-failed'
    //                         ), 'bottom',
    //                         'left', $scope
    //                         .toastTargetId);
    //                 }
    //             }
    //         );
    //     }
    // };

    $scope.changeFocus = function() {
        if ($scope.attributeUpdateFormGroup.get(
                'currentValue').value === $scope
            .originalValue) {
            $scope.isFocused = false;
        }
    }
    $scope.openDialog = function() {
        console.log("Open dialog: ", $container);
        var infoDialogTemplate =
            '<div id="exampleModal">' +
            '<mat-toolbar fxLayout="row" color="accent">' +
            '<h2>Control Device</h2>' +
            '<span fxFlex></span>' +
            '<button mat-button mat-icon-button (click)="cancel()" type="button">' +
            '<mat-icon class="material-icons">close</mat-icon>' +
            '</button>' +
            '</mat-toolbar>' +
            '<mat-progress-bar color="warn" mode="indeterminate" *ngIf="isLoading$ | async">' +
            '</mat-progress-bar>' +
            '<div style="height: 4px;" *ngIf="!(isLoading$ | async)"></div>' +
            '<div mat-dialog-content>' +
            $scope.actionAll() +
            '<table class="table">' +
            '<thead>' +
            '<tr>' +
            '<th scope="col">STT</th>' +
            '<th scope="col">Đối tượng</th>' +
            '<th scope="col">Thiết bị</th>' +
            '<th scope="col">Điều Khiển</th>' +
            '</tr>' +
            '</thead>' +
            '<tbody>' +
            $scope.infoTable($scope.relationData) +
            '</tbody>' +
            '</table>' +
            `<div tb-toast toastTarget=${$scope.toastTargetId}></div>` +
            '</div>' +
            '</div>';

        // var myModal = document.getElementById(
        //     'exampleModal').outerHTML;

        console.log("My modal: ", infoDialogTemplate);
        dialog = customDialog.customDialog(
            infoDialogTemplate,
            $scope.controlDialog).subscribe();
        console.log("open dialog end: ", dialog);

    }
    $scope.actionAll = function() {
                var info =  `<div class="d-flex justify-content-end">
                        <button id="start" (click)="updateAllAttribute(1)"
                              type="button"
                              class="btn btn-primary btn-sm" style="margin-right:5px">Start All</button>
                        <button id="restart" (click)="updateAllAttribute(2)"
                              type="button"
                              class="btn btn-success btn-sm"  style="margin-right:5px">Restart All</button>
                        <button id="stop" (click)="updateAllAttribute(0)"
                              type="button"
                              class="btn btn-danger btn-sm"  style="margin-right:5px">Stop All</button>
                        <button id="resume" (click)="updateAllAttribute(3)"
                              type="button"
                              class="btn btn-secondary btn-sm"  style="margin-right:5px">Resume All</button>
                        <button id="pause" (click)="updateAllAttribute(4)"
                              type="button"
                              class="btn btn-warning btn-sm"  style="margin-right:5px">Pause All</button>
                   </div>`;
        return info;
    }
    $scope.infoTable = function(data) {
        console.log("Info Table: ", data);
        var info = '';
        data.forEach((d, index) => {
            
            info = info + `<tr>
                <th scope="row">${index + 1}</th>
                <td>${d.toName}</td>`;
        if(d.device) {
            var deviceEntityId = `'${d.device[0].to.id}'`;
            var deviceEntityType = `'${d.device[0].to.entityType}'`;
            var assetId = `'${d.to.id}'`;
            
            info = info + `<td>${d.device[0].toName}</td>
                <td>
                    <button id="start" (click)="updateAttribute(${assetId}, ${deviceEntityId}, ${deviceEntityType}, 1)"
                          type="button"
                          class="btn btn-primary btn-sm" style="margin-right:5px">Start</button>
                    <button id="restart" (click)="updateAttribute(${assetId}, ${deviceEntityId}, ${deviceEntityType}, 2)"
                          type="button"
                          class="btn btn-success btn-sm"  style="margin-right:5px">Restart</button>
                    <button id="stop" (click)="updateAttribute(${assetId}, ${deviceEntityId}, ${deviceEntityType}, 0)"
                          type="button"
                          class="btn btn-danger btn-sm"  style="margin-right:5px">Stop</button>
                    <button id="resume" (click)="updateAttribute(${assetId}, ${deviceEntityId}, ${deviceEntityType}, 4)"
                          type="button"
                          class="btn btn-secondary btn-sm"  style="margin-right:5px">Resume</button>
                    <button id="pause" (click)="updateAttribute(${assetId}, ${deviceEntityId}, ${deviceEntityType}, 3)"
                          type="button"
                          class="btn btn-warning btn-sm"  style="margin-right:5px">Pause</button>
                </td>
            </tr>`;
        } else {
            info = info + `<td></td>
                            <td></td>`
        }     
        
        });
        return info;
    }
    $scope.controlDialog = function(instance) {
        console.log("Control Dialog: ", instance);
        let vm = instance;
        vm.cancel = function() {
            console.log("cancel");
            vm.dialogRef.close(null);
        };
        vm.updateAttribute =  async function(assetId, entityId,
            entityType, index) {
            var assetInfo;
            const myPromise = new Promise((resolve, reject) => {
              assetService.getAssetInfo(assetId).subscribe(d => {
                console.log('Asset infor: ', d);
                assetInfo = d;
                resolve();
                });
            });
            await myPromise;
            const entity = {
                id: entityId,
                entityType: entityType
            };
            var state = {
            "Cmd": index,
            "Alert": 0
            };
            console.log("Entity: ", entity);
            console.log($scope.data);
        $scope.data.forEach((d, index) => {
            if(index > 2)
                state[d.dataKey.name] = d.data[0][1];
        });
        console.log('Assetinfor: ', assetInfo);
        if(assetInfo.type == 'production lines') {
            state['Line_Id'] = assetInfo.name;
        } else if(assetInfo.type == 'machine_asset') {
            state['Machine_Id'] = assetInfo.name;
        }
        console.log("State: ", state);
            $scope.isFocused = false;
            if ($scope.entityDetected) {
                var datasource = self.ctx
                    .datasources[0];
                
                attributeService
                    .saveEntityAttributes(
                        entity,
                        'SERVER_SCOPE',
                        [{
                            key: "state",
                            value: state
                        }]
                    ).subscribe(
                        function success() {
                            console.log(
                                "Update Success"
                            );
                            $scope.originalValue =
                                $scope
                                .attributeUpdateFormGroup
                                .get('currentValue')
                                .value;
                            if (settings
                                .showResultMessage
                            ) {
                                $scope
                                    .showSuccessToast(
                                        translate
                                        .instant(
                                            'widgets.input-widgets.update-successful'
                                        ), 1000,
                                        'bottom',
                                        'left',
                                        $scope
                                        .toastTargetId
                                    );
                            }
                        },
                        function fail() {
                            if (settings
                                .showResultMessage
                            ) {
                                $scope
                                    .showErrorToast(
                                        translate
                                        .instant(
                                            'widgets.input-widgets.update-failed'
                                        ), 'bottom',
                                        'left',
                                        $scope
                                        .toastTargetId
                                    );
                            }
                        }
                    );
            }
        };
        vm.updateAllAttribute = function(index) {
            $scope.relationData.forEach(d => {
                if(d.device) {
                    vm.updateAttribute(d.to.id, d.device[0].to.id, d.device[0].to.entityType, index);
                }
            })
        }
    }
    // $scope.openDialog();
    // $scope.cancel = function() {
    //     console.log("AAAA");
    //     dialog.close(null);
    // }
}
// self.controlDialog = function(instance) {
//     console.log("Control Dialog");
//         let vm = instance;

//         vm.cancel = function() {
//             console.log("cancel");
//             vm.dialogRef.close(null);
//         };
