                $scope.attributeUpdateFormGroup.get(
                    'currentValue').patchValue(
                    correctValue($scope
                        .originalValue));
                self.ctx.detectChanges();
            }
        }
    } catch (e) {
        console.log(e);
    }
}

function correctValue(value) {
    if (typeof value !== "number") {
        return 0;
    }
    return value;
}

